<script lang="ts">
    import { onMount } from 'svelte'
    import { axios } from '$lib/common/utils/axios'
    import { fly } from 'svelte/transition'

    const greetings = [
        'Yoloooo',
        'Ï€ = 3.1415926...Oh shit I forgot the rest',
        'Hi !',
        '???',
        '@&*$#&*#*ðŸ’€',
        'Meow Meow...',
        'I know everything',
    ]
    const catFacts = [
        'Cats make about 100 different sounds. Dogs make only about 10',
        'Owning a cat can reduce the risk of stroke and heart attack by a third',
        'It was illegal to slay cats in ancient Egypt',
        'The first known cat video was recorded in 1894',
        'Cats can jump up to six times their length',
        'Cats have 230 bones, while humans only have 206',
        'Some cats can swim (not me)',
    ]

    let showChat = $state(false)
    let nyanClicked = false
    let chatTimeout: ReturnType<typeof setTimeout> = setTimeout(() => {})
    let message = $state('')
    let meowSound: HTMLAudioElement

    onMount(() => {
        meowSound = new Audio('/sounds/meow-sound.mp3')
        meowSound.volume = 0.1
        setTimeout(() => {
            if (!nyanClicked) {
                setChatMessage("Touch me and I'll tell you something")
                showChat = true
            }
        }, 5000)
    })

    function setChatMessage(msg: string, timeout = 4000) {
        message = msg
        chatTimeout = setTimeout(() => (showChat = false), timeout)
    }

    function onNyanClick() {
        meowSound.play()
        nyanClicked = true

        if (showChat) {
            clearTimeout(chatTimeout)
            showChat = false
        }
        randomMessage().then(() => {
            showChat = true
        })
    }

    function randomElement(arr: Array<any>) {
        return arr[Math.floor(Math.random() * arr.length)]
    }

    async function randomMessage() {
        try {
            let rand = Math.random()
            if (rand <= 0.2) {
                let res = await axios.get('http://numbersapi.com/random/math')
                setChatMessage(res.data.slice(0, -1), 7000)
            } else if (rand <= 0.2) {
                let res = await axios.get('http://numbersapi.com/random/trivia')
                setChatMessage(res.data.slice(0, -1), 7000)
            } else if (rand <= 0.45) {
                setChatMessage(randomElement(catFacts), 6000)
            } else {
                throw new Error('default message')
            }
        } catch (err) {
            console.log(err)
            setChatMessage(randomElement(greetings), 4000)
        }
    }
</script>

<!-- svelte-ignore a11y_click_events_have_key_events -->
<!-- svelte-ignore a11y_no_static_element_interactions -->
<div class="nyan" onclick={onNyanClick}>
    {#key message}
        {#if showChat}
            <div class="nyan-chat" transition:fly={{ y: 10 }}>{message}</div>
        {/if}
    {/key}
</div>

