    .nyan {
        position: relative;
        background: url('data:image/gif;base64,');
        width: 34px;
        height: 21px;
        transform: translateY(-1.4em);
        animation: nyan-move 24s linear 2s infinite;
        margin-left: -50px;

        cursor: pointer;
    }
    .nyan-chat {
        position: absolute;
        bottom: 2.4em;
        // right: -5em;
        background: #fed5d5;
        border: 2px solid #fed5d5;
        padding: 5px 10px 3px 10px;
        border-radius: 20px;

        max-width: 140px;
        width: max-content;
        overflow-wrap: break-word;

        text-align: center;
        font-size: 0.8em;
        // font-family: $heading-font;

        transition: all 0.2s;
        &:after {
            content: '';
            position: absolute;
            bottom: 2px;
            left: 50%;
            width: 0;
            height: 0;
            border: 12px solid transparent;
            border-top-color: #fed5d5;
            border-bottom: 0;
            border-left: 0;
            margin-left: -6px;
            margin-bottom: -12px;
            z-index: -1;
        }
    }
    @keyframes bounce-in {
        0% {
            transform: scale(0);
        }
        50% {
            transform: scale(1.25);
        }
        100% {
            transform: scale(1);
        }
    }
    @keyframes nyan-move {
        0% {
            margin-left: -50px;
        }
        // 3% {
        //     margin-left: 0px;
        // }
        100% {
            margin-left: calc(100vw + 5em);
        }
    }
</style>
